import{_ as d,C as t,c as r,o as n,j as a,G as i,aI as s,a as o}from"./chunks/framework.zsPSsrbh.js";const b="/CreatorBox/images/readme_main_en.png",v=JSON.parse('{"title":"🎬 What is CreatorBox?","description":"","frontmatter":{},"headers":[],"relativePath":"what-is-creatorbox.md","filePath":"what-is-creatorbox.md","lastUpdated":1758094358000}'),p={name:"what-is-creatorbox.md"};function k(m,e,u,h,g,f){const l=t("NolebasePageProperties"),c=t("Gallery");return n(),r("div",null,[e[0]||(e[0]=a("h1",{id:"🎬-what-is-creatorbox",tabindex:"-1"},[o("🎬 What is CreatorBox? "),a("a",{class:"header-anchor",href:"#🎬-what-is-creatorbox","aria-label":'Permalink to "🎬 What is CreatorBox?"'},"​")],-1)),i(l),e[1]||(e[1]=s('<p><strong>CreatorBox</strong> is designed for creators, focusing on solving repetitive tasks in content creation workflows such as <code>video translation</code>, <code>cross-language dubbing</code>, and <code>personalized dubbing</code>. Simply provide the materials, and it will automatically complete speech recognition, translation, synthesis, and draft export, enhancing creation efficiency and unleashing creative potential!</p><h2 id="📦-quick-start" tabindex="-1">📦 Quick Start <a class="header-anchor" href="#📦-quick-start" aria-label="Permalink to &quot;📦 Quick Start&quot;">​</a></h2><p>👉 Installation &amp; Deployment Guide: <a href="/CreatorBox/deploy-local">Local Installation</a> | <a href="/CreatorBox/deploy-colab">Remote Deployment</a></p><p>👉 Development &amp; Integration Guide: <a href="/CreatorBox/gradio">Application Plugins</a></p><div class="tip custom-block" style="padding-top:8px;"><p><strong>CreatorBox</strong>, designed for creators, enhances creation efficiency and unleashes creative potential.</p></div><p><img src="'+b+'" alt="main" loading="lazy"></p><h2 id="📤-gradio-apps" tabindex="-1">📤 Gradio Apps <a class="header-anchor" href="#📤-gradio-apps" aria-label="Permalink to &quot;📤 Gradio Apps&quot;">​</a></h2>',7)),i(c,{images:["/CreatorBox/images/gradio_app_cme.png","/CreatorBox/images/gradio_app_dlp.png","/CreatorBox/images/gradio_app_imr.png","/CreatorBox/images/gradio_app_sdr.png","/CreatorBox/images/gradio_app_uvr.png","/CreatorBox/images/gradio_app_vmr.png","/CreatorBox/images/gradio_app_vsd.png","/CreatorBox/images/gradio_app_vse.png"],captions:["Capcut Material Extractor","YouTube Downloader Plus (yt-dlp)","Image Mask Removal","Silence Detect Removal","Ultimate Vocal Remover (v5)","Video Mask Removal","Video Scene Detect","Video Subtitle Extractor"]}),e[2]||(e[2]=a("h2",{id:"📺-video-demonstration",tabindex:"-1"},[o("📺 Video Demonstration "),a("a",{class:"header-anchor",href:"#📺-video-demonstration","aria-label":'Permalink to "📺 Video Demonstration"'},"​")],-1)),e[3]||(e[3]=a("p",null,[a("div",{class:"embed-responsive embed-responsive-16by9"},[a("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"100%",height:"387px",src:"https://www.youtube.com/embed/PERwuqvvFv8?autoplay=0&start=0",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})])],-1)),e[4]||(e[4]=s('<h2 id="🎨-what-can-it-do" tabindex="-1">🎨 What Can It Do? <a class="header-anchor" href="#🎨-what-can-it-do" aria-label="Permalink to &quot;🎨 What Can It Do?&quot;">​</a></h2><ul><li><p>🎥 <strong>Content Creators</strong>: Optimize video dubbing, translation, and editing workflows, enhancing creation efficiency and unleashing creative potential.</p></li><li><p>🌍 <strong>Multilingual Translation/Dubbing</strong>: Operate localized content creation and cross-language publishing.</p></li><li><p>⚙️ <strong>Independent Deployment</strong>: Deploy locally with flexible configurations to ensure privacy.</p></li></ul><h2 id="🎯-features" tabindex="-1">🎯 Features <a class="header-anchor" href="#🎯-features" aria-label="Permalink to &quot;🎯 Features&quot;">​</a></h2><ul><li><p><strong>🎤 Subtitle Recognition</strong></p><p>Accurately transcribe voices from videos and audio, with flexible configuration to adapt to different devices and scenarios, ensuring high-quality text generation</p></li><li><p><strong>🌐 Language Translation</strong></p><p>Translate between multiple languages, switch translation providers, and adjust advanced parameters to optimize translation results and overcome language barriers</p></li><li><p><strong>🎧 Speech Synthesis</strong></p><p>A rich library of voices and customization options for personalized dubbing experiences, meeting creative needs with real-time previews for precise creation</p></li><li><p><strong>✂️ Draft Editing</strong></p><p>Export materials to editing tools with multi-dimensional control over visuals, audio, and subtitles, aiding post-production adjustments and customization</p></li><li><p><strong>🧩 Application Components</strong></p><p>Built-in application components for efficient collaboration and flexible usage, catering to diverse user needs</p></li><li><p><strong>🔧 Preview and Debugging</strong></p><p>Flexible and efficient configuration adjustments to ensure perfect presentation at every stage, enhancing creation efficiency and quality</p></li></ul><h2 id="📅-planned-support" tabindex="-1">📅 Planned Support <a class="header-anchor" href="#📅-planned-support" aria-label="Permalink to &quot;📅 Planned Support&quot;">​</a></h2><h3 id="subtitles" tabindex="-1">Subtitles <a class="header-anchor" href="#subtitles" aria-label="Permalink to &quot;Subtitles&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-0" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-0"> Multi-Provider Support: Switch between <code>Srt</code>, <code>Capcut</code>, <code>FunAsr</code>, and <code>FasterWhisper</code> for processing.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-1" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-1"> Video Download: Automatically download videos from <code>YouTube</code> and <code>TikTok</code>.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-2" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-2"> Video-to-Text Transcription: Transcribe audio from videos into text and extract subtitles.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-3" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-3"> Multi-Track Separation: Separate multiple audio tracks including <code>vocals</code>, <code>accompaniment</code>, <code>drums</code>, <code>bass</code>, etc.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-4" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-4"> Speaker Recognition &amp; Alignment: Extract speaker voiceprints and align audio precisely with subtitle text.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-5" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-5"> Multi-Speaker Detection: Automatically identify multiple speakers in a video.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-6" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-6"> Emotion Recognition: Recognize emotions such as <code>Angry</code>, <code>Disgusted</code>, <code>Fearful</code>, <code>Happy</code>, <code>Neutral</code>, <code>Other</code>, <code>Sad</code>, and <code>Surprised</code>.</label></li></ul><h3 id="translation" tabindex="-1">Translation <a class="header-anchor" href="#translation" aria-label="Permalink to &quot;Translation&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-7" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-7"> Multiple Providers: Support switching between <code>OpenAi</code>, <code>Gemini</code> and <code>DashScope</code></label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-8" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-8"> Custom Models and Prompt Commands</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-9" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-9"> Batch Processing for Long Texts</label></li></ul><h3 id="speech" tabindex="-1">Speech <a class="header-anchor" href="#speech" aria-label="Permalink to &quot;Speech&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-10" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-10"> Multi-Provider Support: Switch between <code>AzureTTS</code>, <code>EdgeTTS</code>, <code>ElevenLabs</code>, <code>GPTSoVITS</code>, and <code>CosyVoice2</code> for voice synthesis.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-11" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-11"> Real-Time Voice Preview: Supports real-time voice synthesis and preview for quick evaluation.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-12" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-12"> Popular Voices: Provides 559 built-in voices based on <code>AzureTTS</code> and <code>EdgeTTS</code>, with over 895 sample recordings. Common voices include but are not limited to <code>Xiaomei</code>, <code>Xiaoshuai</code>, <code>Dashan</code>, <code>Dazhuang</code>, <code>General Arthur</code>, etc.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-13" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-13"> Voice Cloning: Perform voice cloning using <code>CosyVoice2</code> and <code>GPTSoVITS</code>, supporting voice command control, cross-language cloning, and emotion-aware synthesis.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-14" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-14"> Fine-Tuning Training: Supports emotion fine-tuning and inference on <code>GPTSoVITS</code> baseline models (<code>v1</code>, <code>v2</code>, <code>v2Pro</code>, <code>v2ProPlus</code>, <code>v3</code>, <code>v4</code>) to create personalized voice effects.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-15" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-15"> Extended Capabilities: Access the <code>ElevenLabs</code> voice library and support voice cloning to easily expand voice expressiveness.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-16" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-16"> Custom Voice Library: Built-in three types of voices: <code>Built-in</code>, <code>Video</code>, and <code>Custom</code>, suitable for various scenarios.</label></li></ul><h3 id="draft" tabindex="-1">Draft <a class="header-anchor" href="#draft" aria-label="Permalink to &quot;Draft&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-17" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-17"> Track Control: Supports up to <code>6</code> tracks for <code>Visuals</code>, <code>Audio</code>, and <code>Subtitles</code></label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-18" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-18"> Subtitle Generation: Customize <code>Size</code>, <code>Position</code>, <code>Color</code>, and <code>Outline</code> settings</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-19" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-19"> Volume Adjustment: Control <code>Original Sound</code>, <code>Speech</code>, and <code>Background Music</code> volumes</label></li></ul><h3 id="others" tabindex="-1">Others <a class="header-anchor" href="#others" aria-label="Permalink to &quot;Others&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-20" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-20"> Dubbing Modes: Choose between <code>Video</code>, <code>Audio</code>, and <code>Adaptive</code> modes</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-21" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-21"> Translation Modes: Translate videos from the original language to another</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-22" disabled="disabled"><label class="task-list-item-label" for="task-item-22"> Narration Mode: Planned...</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-23" disabled="disabled"><label class="task-list-item-label" for="task-item-23"> Automated Posting</label></li></ul><h2 id="📢-feedback-and-suggestions" tabindex="-1">📢 Feedback and Suggestions <a class="header-anchor" href="#📢-feedback-and-suggestions" aria-label="Permalink to &quot;📢 Feedback and Suggestions&quot;">​</a></h2><ul><li><p>Submit via <a href="https://github.com/xiesx123/CreatorBox/issues" target="_blank" rel="noreferrer">Issues</a> or <a href="mailto:xiesx123@gmail.com?subject=CreatoxBox%20Discussions&amp;body=Hello,%20I%20would%20like%20to%20inquire%20about%20your%20project.%20Could%20you%20provide%20more%20details?" target="_blank" rel="noreferrer">Email</a>.</p></li><li><p>Join <a href="https://discord.gg/kt6JWax3P6" target="_blank" rel="noreferrer">Discord</a> for discussions on usage or new features.</p></li></ul>',17))])}const y=d(p,[["render",k]]);export{v as __pageData,y as default};
